#!/usr/bin/env sh

# Synopsis:
# Test the test runner by running it against a predefined set of solutions 
# with an expected output.

# Output:
# Outputs the diff of the expected test results against the actual test results
# generated by the test runner.

# Example:
# ./bin/run-tests.sh

exit_code=0

wget https://github.com/exercism/ballerina/archive/main.tar.gz
tar -xf main.tar.gz 

# Iterate over all test directories
work_dir=$(pwd)
for exercise_dir in ballerina-main/exercises/**/*; do
    dir_name=$(basename "${exercise_dir}")
    dir_path=$(realpath "${exercise_dir}")
    if [ -d $dir_path ]; then
        echo "==== ${dir_name}: building ${dir_path}"
        
        ls $dir_path
        cd $dir_path
        # Copy the reference files if they exist
        if [ -d ".meta/reference" ]; then
            yes | cp -r .meta/reference/* ./
        fi
        # Build the project
        bal test 
        if [ $? -ne 0 ]; then
            exit_code=1
        fi
        cd $work_dir
    fi
done

echo "build finished with exit code: ${exit_code}"
# exit ${exit_code}
